from __init__ import *

svg = Svg(100, 100, "0 0 100 100")
print("Svg is created.")
defs = Defs(svg)
print("Defs created.")
radial = RadialGradient(defs, "rad1")
print("Radial Gradient created.")
stop1 = Stop(radial, "0%", "#000000", "0.5")
stop2 = Stop(radial, "50%", "#ff0000", "0.7")
stop3 = Stop(radial, "100%", "#0000ff", "1")
print("Stops of Radial Gradient is created.")
linear = LinearGradient(defs, "lin1")
print("Linear Gradient created.")
stop1 = Stop(linear, "0%", "#000000", "0.5")
stop1 = Stop(linear, "50%", "#00ff00", "0.7")
stop1 = Stop(linear, "100%", "#dadada", "1")
print("Stops of Linear Gradient is created.")
rect = Rect(svg, 100, 100, style={"fill": "#dadada"})
print("Rect is created.")
polygon = Polygon(svg, [(0,100), (0, 50), (70, 50), (70, 100)], style={"fill": "url(#lin1)", "stroke": "#000000", "stroke-width": 2})
print("Polygon is created")
circle = Circle(svg, 50, 50, 10, style="fill:url(#rad1);stroke-width:15.1181")
print("Circle is created.")
ellipse = Ellipse(svg, 10, 10, 5, 12, style={"fill":"#0000ff"})
print("Ellipse is created.")
line = Line(svg, 0,100,100,0, style={"stroke-width":3, "stroke": "#000000"})
print("Line is created.")
polyline = Polyline(svg, "20,20 40,25 60,40 80,120 120,140 200,180", style="fill:none;stroke:black;stroke-width:3")
print("Polyline is created.")
path = Path(svg, d="M10 0 L5 20 L25 20 Z", style="fill:#ff00ff;")
print("Path is created.")
text = Text(svg, "Created", x=25, y=25, style={"fill": "#123456"})
print("Text is created.")

print(svg.create_tag(), file=open("test.svg", "w"))
