class Strain:
    def __init__(self, _name, slug, species, description,
    genetic_cultivation_description, references, hero_image_attribution, lab_data_attribution, thc_min,
    thc_max, cbd_min, cbd_max, featured_position, featured,
    avatar_image_url, hero_image_url):
        self.name = _name
        self.slug = slug
        self.species = species
        self.description = description
        self.genetic_cultivation_description = genetic_cultivation_description
        self.references = references
        self.hero_image_attribution = hero_image_attribution
        self.lab_data_attribution = lab_data_attribution
        self.thc_min = thc_min
        self.thc_max = thc_max
        self.cbd_min = cbd_min
        self.cbd_max = cbd_max
        self.featured_position = featured_position
        self.featured = featured
        self.avatar_image_url = avatar_image_url
        self.hero_image_url = hero_image_url




